import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:5000/api",
});


API.interceptors.request.use((config) => {
  const token = localStorage.getItem("token")
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

export const register = (data) => API.post("/register", data);
export const login = (data) => API.post("/login", data);
export const getProfile = () => API.get("/auth/me")//

export const getblog = (params, data) =>
  API.get(`/blogs?${params}`, data);

export const getsingleblog = (id, data) =>
  API.get(`/blogs/${id}`, data);

export const likeBlog = (id, data) =>
  API.post(`/blogs/${id}/like`, data);

export const commentBlog = (id, data) =>
  API.post(`/blogs/${id}/comments`, data);

export const deleteBlog = (id, data) =>
  API.delete(`/blogs/${id}`, data);


export const getUserblog = ( data) =>  API.get("/users/blogs", data);

export const createblog = (data) => API.post("/blogs", data);

export const updateblog = (id, data) => API.put(`/blogs/${id}`, data);

export const getblogAdmin = ( data) =>  API.get("/admin/blogs", data);

export const getUserAdmin = ( data) =>  API.get("/admin/users", data);

export const deleteBlogAdmin = (id, data) =>
  API.delete(`/blogs/${id}`, data);
